    return this.animationMode !== 'NoopAnimations' && this.animated;
  }

  constructor(
    private elementRef: ElementRef<HTMLElement>,
    private ngZone: NgZone,
    @Optional() @Inject(ANIMATION_MODULE_TYPE) public animationMode?: string
  ) {}

